import "./PageArtiste.css"
import React from "react";
import { useParams } from "react-router-dom"


export default function PageArtiste () {

    const [artistesData, setArtistesData] = React.useState()

    const {artistTitle} = useParams();


    React.useEffect(() => {
       
        fetch(`http://drupal10/jsonapi/node/artistes`)
            .then(res => res.json())
            .then(data => setArtistesData(data.data))
    }, []);

    console.log(artistesData);


    return(
        <div className="pageArtist">
            <div className="pageArtistContainer">
                <img className="artistImage" src="/images/musician-g66fdd8f28_1280.jpg" alt="Jacque Chirac avec sa guitare"></img>
                <div className="pageArtistText">
                    <h1 className="pageArtistTitle">Jacque Chirac</h1>
                    <p>Scène numéro 1</p>
                    <p>18H</p>
                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Aenean et ante suscipit, mollis est quis, auctor metus. Integer nec pellentesque nunc. Fusce blandit nec purus quis varius. Fusce massa dolor, condimentum in varius vel, ornare nec lorem. Quisque non ipsum diam. Cras ac neque iaculis, hendrerit est in, rutrum nibh. Vestibulum dignissim sit amet est eu suscipit.</p>
                </div>
            </div>
            
            
        </div>
    );
}
