import React from "react";
import "./Programmation.css"

export default function Programmation () {


    const [testItem, setTestItem] = React.useState([1,1,1,1,1,1,1]);
    const [daySelected, setDaySelected] = React.useState(1);

    function handleRadioChange (event) {
        setDaySelected(() => {
            event.target.value
        })
    }

    const artiste = testItem.map(data => {
        return (
            <div className="artistContainer">
                <div>
                    <img className="miniature" src="/images/musician-g66fdd8f28_1280.jpg" alt="Artiste"></img>
                    <div className="infoMiniature">
                        <h2>Jacque Chirac</h2>
                        <p>Scène numéro 1</p>
                        <p>18H</p>
                    </div>
                </div>
                
                
            </div>
        );
    });
    

    

    return(
        <div className="programmation">
            <h1>Programmation</h1>
            <select></select>

            <div className="radioContainer">

                <input 
                    className="radioProgrammation" 
                    type="radio" 
                    id="jour1" 
                    name="jours"
                    value="1"
                    onChange={handleRadioChange}
                />
                <label htmlFor="jour1">Jour 1</label> 

                <input 
                    className="radioProgrammation" 
                    type="radio" 
                    id="jour2" 
                    name="jours"
                    value="2"
                    onChange={handleRadioChange}
                />
                <label htmlFor="jour2">Jour 2</label>     

                <input 
                    className="radioProgrammation" 
                    type="radio" 
                    id="jour3" 
                    name="jours"
                    value="3"
                    onChange={handleRadioChange}
                />
                <label htmlFor="jour3">Jour 3</label>

            </div>

            <div className="miniatureGrid">
                {artiste}
            </div>

        </div>
    );
}
